public class Ex_4_If_else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		/*
		 * have 2 number already declared and assigned values using multi if staement,
		 * chaek if num1= num2 equal or not,
		 * 
		 */

		int num1 = 10;
		int num2 = 10;

		if (num1 == num2) {

			System.out.println(num1 + " and " + num2 + " are equal");
			
		} else if (num1 > num2) {
			
			System.out.println(num1 + " is greater then " + num2);

		} else if (num1<num2) {
			
			
			System.out.println(num1 + " is less then " + num2);
		
		}
		
			
			
		}

}
